mapper.select:
select

from SP_COMMODITY_INFO
where SVR_COMMODITY_ID=#{svrCommodityId,jdbcType=VARCHAR}
and SMERCHANT_ID=#{smerchantId,jdbcType=VARCHAR}
and IDEL_FLAG=0

mapper.update:
update SP_COMMODITY_INFO
set SLABEL_ID="" , SLABEL_NAME=""
where SLABEL_ID=#{slabelId,jdbcType=VARCHAR}

update SP_COMMODITY_INFO
set
SBRAND_ID="",SBRAND_NAME="",TUPDATE_TIME=#{tupdateTime,jdbcType=TIMESTAMP},SUPDATE_USER=#{supdateUser,jdbcType=VARCHAR}
where SBRAND_ID=#{sbrandId,jdbcType=VARCHAR}
AND SMERCHANT_ID=#{smerchantId,jdbcType=VARCHAR}
AND IDEL_FLAG=0

update SP_COMMODITY_INFO
set
SBIG_CATEGORY_ID="",SBIG_CATEGORY_NAME="",TUPDATE_TIME=#{tupdateTime,jdbcType=TIMESTAMP},SUPDATE_USER=#{supdateUser,jdbcType=VARCHAR}
where SBIG_CATEGORY_ID=#{sbigCategoryId,jdbcType=VARCHAR}
AND SMERCHANT_ID=#{smerchantId,jdbcType=VARCHAR}
AND IDEL_FLAG=0

update SP_COMMODITY_INFO
set
SSMALL_CATEGORY_ID="",SSMALL_CATEGORY_NAME="",TUPDATE_TIME=#{tupdateTime,jdbcType=TIMESTAMP},SUPDATE_USER=#{supdateUser,jdbcType=VARCHAR}
where SSMALL_CATEGORY_ID=#{ssmallCategoryId,jdbcType=VARCHAR}
AND SMERCHANT_ID=#{smerchantId,jdbcType=VARCHAR}
AND IDEL_FLAG=0

